function depPlace(line){
	document.getElementById("pgOne").style.display = "none";
	document.getElementById("line").innerHTML = line;
	var divName = line + "Div";
	document.getElementById(divName).style.display = "block";
	document.getElementById("lin").innerHTML = mtrLineName(line);
}

function aschQuery(station){
	var line = document.getElementById("line").innerHTML;
	document.getElementById("stat").style.display = "none";
	document.getElementById("loading").style.display = "block";
	var sta = mtrStationCode(station);
	document.getElementById("lin").innerHTML = sta + "站";
	
	const name = line + "-" + station;
	
	const url = "https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=" + line + "&sta=" + station;
	const xhttpr = new XMLHttpRequest();
	xhttpr.open("GET", url, true);
	
	xhttpr.send();
	
	xhttpr.onload = ()=> {
		if (xhttpr.status == 200){
			const response = JSON.parse(xhttpr.response);
			const list = response["data"];
			const route = list[name];
			const up = route["UP"];
			const down = route["DOWN"];
			
			let len = "";
			let leng = "";
			if (up != null){
				len = up.length;
			}
			if (down != null){
				leng = down.length;
			}
			var x = "";
			
			if (len != ""){     
				var x = '<tr><td><strong>上行</strong></td></tr>';
				var x = x +'<tr><td><strong>抵站時間</strong></td><td><strong>月台</strong></td><td><strong>終點站</strong></td></tr>';

				for (i = 0; i < len; i++){
					var route2 = up[i];
					var platform = route2["plat"];
					var etaTime = route2["time"];
					var destination = route2["dest"];
	
					var outputDestination = mtrStationCode(destination);

					var platformNo = platform + "號月台";

					var etaDate = new Date(etaTime);
					var time = etaDate.toLocaleTimeString('en-HK', {hourCycle: 'h23'});

					var x = x + "<tr><td>" + time + "</td><td>" + platformNo + "</td><td>" + outputDestination + "</td></tr>";
				}
			}

			if (x == undefined){
				var x = "";
			}

			if (leng != ""){
				var x = x + '<tr><td><strong>下行</strong></td></tr>';
				var x = x + '<tr><td><strong>抵站時間</strong></td><td><strong>月台</strong></td><td><strong>終點站</strong></td></tr>';

				for (y = 0; y < leng; y++){
					var route2 = down[y];
					var platform = route2["plat"];
					var etaTime = route2["time"];
					var destination = route2["dest"];

					var outputDestination = mtrStationCode(destination);

					var platformNo = platform + "號月台";

					var etaDate = new Date(etaTime);
					var time = etaDate.toLocaleTimeString('en-HK', {hourCycle: 'h23'});

					var x = x + "<tr><td>" + time + "</td><td>" + platformNo + "</td><td>" + outputDestination + "</td></tr>";
				}
			}
			
			if (x == ""){
				var x = '<tr><td><strong>No information</strong></td></tr>';
			}
			document.getElementById("listTable").innerHTML = x;
			document.getElementById("routeList").style.display = "block";

			document.getElementById("loading").style.display = "none";
		} else {
			//idk do sth
		}
	}
}

function schQuery(stopId){
	let distance, stop, stop_id, allLine = [], apiReceived = 0, x = "", y = "<div class='centerDiv'>", note = "";
	
	document.getElementById("stat").style.display = "none";
	document.getElementById("loading").style.display = "block";
		
	for (let i = 0; i < response.length; i++){
		if (stopId == response[i]["code"]){
			allLine.push({line: response[i]["line"], sta: response[i]["code"], name: response[i]["name"]});
		}
	}
	
	for (let i = 0; i < allLine.length; i++){
		const url = "https://rt.data.gov.hk/v1/transport/mtr/getSchedule.php?line=" + allLine[i]["line"] + "&sta=" + allLine[i]["sta"];
		const xhttpr = new XMLHttpRequest();
		let lineStation = allLine[i]["line"] + "-" + allLine[i]["sta"], destination = [], chunk = "", etaTime;
		xhttpr.open("GET", url, true);

		xhttpr.send();

		xhttpr.onload = ()=> {
			if (xhttpr.status == 200){
				raw = JSON.parse(xhttpr.response);
				apiReceived++;
				if (raw["status"] == 0){
					x += "<table id='" + allLine[i]["line"] + "' class='timetable' style='display: none'><tr><td>Error: " + raw["message"] + "</td></tr></table>"
				} else {
					list = raw["data"][lineStation];
					x = x + "<table id='" + allLine[i]["line"] + "' class='timetable' style='display: none'><tr><td><strong>抵站時間</strong></td><td><strong>終點站</strong></td><td><strong>月台</strong></td></tr>";
					if (list["UP"] != null){
						for (let j = 0; j < list["UP"].length; j++){
							note = "";
							if (allLine[i]["line"] == "EAL"){
								if (list["UP"][j]["route"] == "RAC"){
									note = "<span style='font-size: 75%'>經馬場</span>";
								}
							}
							etaTime = new Date(list["UP"][j]["time"]);
							etaTime = etaTime.toLocaleTimeString('en-HK', {hourCycle: 'h23'});
							chunk += "<tr><td>" + etaTime + "</td><td>" + response[response.map(e => e.code).indexOf(list["UP"][j]["dest"])]["name"] + note + "</td><td>" + list["UP"][j]["plat"] + "</td></tr>";
							destination.push(response[response.map(e => e.code).indexOf(list["UP"][j]["dest"])]["name"]);
						}
						destination = [...new Set(destination)];
						x = x + "<tr><td colspan='3' style='background-color: #339933;'>往： " + destination.join("/") + "</td></tr>" + chunk;
					}
					chunk = "", destination = [];
					if (list["DOWN"] != null){
						for (let j = 0; j < list["DOWN"].length; j++){
							note = "";
							if (allLine[i]["line"] == "EAL"){
								if (list["UP"][j]["route"] == "RAC"){
									note = "<span style='font-size: 75%'>經馬場</span>";
								}
							}
							etaTime = new Date(list["DOWN"][j]["time"]);
							etaTime = etaTime.toLocaleTimeString('en-HK', {hourCycle: 'h23'});
							chunk += "<tr><td>" + etaTime + "</td><td>" + response[response.map(e => e.code).indexOf(list["DOWN"][j]["dest"])]["name"] + note + "</td><td>" + list["DOWN"][j]["plat"] + "</td></tr>";
							destination.push(response[response.map(e => e.code).indexOf(list["DOWN"][j]["dest"])]["name"]);
						}
						destination = [...new Set(destination)];
						x = x + "<tr><td colspan='3' style='background-color: #339933;'>往： " + destination.join("/") + "</td></tr>" + chunk;
					}
					x = x + "</table>";
				}
				y += "<button class='btnOne' onclick='changeTable(\"" + allLine[i]["line"] + "\")' style='border-radius: 5px'>" + mtrLineName(allLine[i]["line"]) + "</button>";
				if (apiReceived == allLine.length){
					document.getElementById("lin").innerText = allLine[0]["name"] + "站";
					document.getElementById("loading").style.display = "none";
					document.getElementById("etaList").innerHTML = y + "</div>" + x;
					document.getElementById(allLine[0]["line"]).style.display = "";
				}
			} else {
				//idk do sth
			}
		}
	}
	console.log(allLine);
}

function changeTable (line){
	const collection = document.getElementsByTagName("table");
	for (let i = 0; i < collection.length; i++){
		collection[i].style.display = "none";
	}
	document.getElementById(line).style.display = "";
}

function hptoHome(){
	window.location.reload();
}

function mtrLineName(code) {
	switch (code){
		case "ISL":
			return "港島線";
		case "AEL":
			return "機場快線";
		case "TCL":
			return "東涌線";
		case "TML":
			return "屯馬線";
		case "TKL":
			return "將軍澳線";
		case "EAL":
			return "東鐵線";
		case "SIL":
			return "南港島線";
		case "TWL":
			return "荃灣線";
		case "KTL":
			return "觀塘線";
		default:
			return "迪士尼線";
	}
}

